datasource.parameter:
${isempty(param.q) ? "" :
concat("(",
	buildFreetextQuery("owner/qualifiedname", param.q, "AND"), 
	" OR ", 
	buildFreetextQuery("modifier/qualifiedname", param.q, "AND"), 
	" OR ", 
	buildFreetextQuery("data/*", param.q, "AND"),
	")"
) }
              ${ stringlength(param.contenttype) != 0 ? concat("AND ", buildFreetextQuery("contenttype", replace(param.contenttype,',',' '), "OR" )) : "" }
